mapper.insert:
insert into customer (

	cust_Num
	,cust_Id
	,cust_Name
	,cust_Pw
	,cust_Sex
	,cust_Birth
	,cust_Address
	,cust_Phone
	,cust_Email
	,cust_Major

)
values (
	cust_Num_sequence.nextval
	,#{cust_Id}
	,#{cust_Name}
	,#{cust_Pw}
	,#{cust_Sex}
	,#{cust_Birth}
	,#{cust_Address}
	,#{cust_Phone}
	,#{cust_Email}
	,#{cust_Major}
)

mapper.update:
UPDATE customer
SET emailVerify = 'Y'
WHERE cust_Email = #{cust_Email}
