mapper.insert:
insert into reservation (
	res_Num
	,res_Date
	,hos_Name
	,cust_Num
	,cust_Id
)
values (
	res_Num_sequence.nextval
	, #{res_Date}
	, #{hos_Name}
	, #{cust_Num}
	, #{cust_Id}	
)

mapper.select:
select res_Num, res_Date, hos_Name, cust_Num, cust_Id
from reservation
where cust_Num = #{cust_Num}
